mapper.sql:

WHERE 1 = 1




		AND id = #{id}


		AND id IN 

			#{id}






	AND edittime >= #{lastservicetime}





		AND delstate = '${@com.fwk.common.util.constants.CommonConstantsUtil@IS_DELETE_FALSE}'


		AND delstate = #{delstate}






		AND (creattime BETWEEN #{createdatestart} AND #{createdateend})


		AND creattime >= #{createdatestart}


		AND creattime <= #{createdateend}





	AND planid = #{planid}

mapper.select:
SELECT * FROM ( SELECT t_f_plan_warn.*, t_c_dict_timeunit.dictname AS timeunitname, t_c_dict_type.dictname AS typename FROM t_f_plan_warn LEFT OUTER JOIN t_c_dict t_c_dict_timeunit ON ( t_c_dict_timeunit.dicttype = '${@com.fwk.common.util.constants.EDictType@farm_time_unit.dicttype}' AND t_f_plan_warn.timeunit = t_c_dict_timeunit.dictvalue ) LEFT OUTER JOIN t_c_dict t_c_dict_type ON ( t_c_dict_type.dicttype = '${@com.fwk.common.util.constants.EDictType@plan_warntype.dicttype}' AND t_f_plan_warn.timeunit = t_c_dict_type.dictvalue ) ) table_all
ORDER BY ${orderBySQL}
ORDER BY id ASC
LIMIT #{page.startRow}, #{page.pageSize}
