mapper.sql:

WHERE 1 = 1




		AND id = #{id}


		AND id IN 

			#{id}






	AND edittime >= #{lastservicetime}





		AND delstate = '${@com.fwk.common.util.constants.CommonConstantsUtil@IS_DELETE_FALSE}'


		AND delstate = #{delstate}






		AND (creattime BETWEEN #{createdatestart} AND #{createdateend})


		AND creattime >= #{createdatestart}


		AND creattime <= #{createdateend}





	AND recordid = #{recordid}




	AND parcelid = #{parcelid}

mapper.select:
SELECT * FROM ( SELECT t_f_record_parcel.*, t_p_parcel.`name` AS parcelname, t_p_parcel.addresscode AS addresscode FROM t_f_record_parcel LEFT OUTER JOIN t_p_parcel ON t_p_parcel.id = t_f_record_parcel.parcelid ) table_all
ORDER BY ${orderBySQL}
ORDER BY id ASC
LIMIT #{page.startRow}, #{page.pageSize}
