mapper.sql:

WHERE 1 = 1




		AND id = #{id}


		AND id IN 

			#{id}






	AND edittime >= #{lastservicetime}





		AND delstate = '${@com.fwk.common.util.constants.CommonConstantsUtil@IS_DELETE_FALSE}'


		AND delstate = #{delstate}






		AND (creattime BETWEEN #{createdatestart} AND #{createdateend})


		AND creattime >= #{createdatestart}


		AND creattime <= #{createdateend}






		AND taskid = #{taskid}


		AND taskid IN 

			#{taskid}







		AND createuserid = #{createuserid}


		AND createuserid IN 

			#{createuserid}







		AND createuserorgid = #{createuserorgid}


		AND createuserorgid IN 

			#{createuserorgid}







		AND createuserdepartmentid = #{createuserdepartmentid}


		AND createuserdepartmentid IN 

			#{createuserdepartmentid}

mapper.select:
SELECT * FROM ( SELECT t_f_task_userpermisions.*, t_u_users.`name` AS username FROM t_f_task_userpermisions LEFT OUTER JOIN t_u_users ON t_f_task_userpermisions.createuserid = t_u_users.id ) table_all
ORDER BY ${orderBySQL}
ORDER BY id ASC
LIMIT #{page.startRow}, #{page.pageSize}
